INSERT INTO `wp_options` VALUES (381,'_transient_ti_about_plugin_info_translatepress-multilingual','O:8:\"stdClass\":20:{s:4:\"name\";s:51:\"TranslatePress &#8211; Translate Multilingual sites\";s:4:\"slug\";s:27:\"translatepress-multilingual\";s:7:\"version\";s:5:\"1.9.5\";s:6:\"author\";s:103:\"<a href=\"https://cozmoslabs.com/\">Cozmoslabs, Razvan Mocanu, Madalin Ungureanu, Cristophor Hurduban</a>\";s:14:\"author_profile\";s:47:\"https://profiles.wordpress.org/madalinungureanu\";s:12:\"contributors\";a:5:{s:10:\"cozmoslabs\";a:3:{s:7:\"profile\";s:41:\"https://profiles.wordpress.org/cozmoslabs\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/83c5f5528e38b95b700e7c6aebb09a06?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:10:\"cozmoslabs\";}s:10:\"razvanmo-1\";a:3:{s:7:\"profile\";s:41:\"https://profiles.wordpress.org/razvanmo-1\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/39a393be9fc95cc1e52ead414b131c32?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:13:\"Razvan Mocanu\";}s:16:\"madalinungureanu\";a:3:{s:7:\"profile\";s:47:\"https://profiles.wordpress.org/madalinungureanu\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/e6c322c984607adc40846b568ffdacf1?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:17:\"madalin.ungureanu\";}s:10:\"sareiodata\";a:3:{s:7:\"profile\";s:41:\"https://profiles.wordpress.org/sareiodata\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/7b677c881daa4d0db09dd5b4c238274c?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:15:\"Cristian Antohe\";}s:10:\"cristophor\";a:3:{s:7:\"profile\";s:41:\"https://profiles.wordpress.org/cristophor\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/46e20a3784cff243bfc8fc6da9b6e110?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:19:\"Cristophor Hurduban\";}}s:12:\"requires_php\";s:6:\"5.6.20\";s:7:\"ratings\";a:5:{i:5;i:259;i:4;i:11;i:3;i:9;i:2;i:8;i:1;i:22;}s:11:\"num_ratings\";i:309;s:15:\"support_threads\";i:206;s:24:\"support_threads_resolved\";i:128;s:15:\"active_installs\";i:100000;s:8:\"homepage\";s:27:\"https://translatepress.com/\";s:8:\"sections\";a:6:{s:11:\"description\";s:8762:\"<p><strong>Experience a better way to translate your WordPress site and go multilingual, directly from the front-end using a friendly user interface.</strong></p>\n<p>TranslatePress is a <a href=\"https://translatepress.com/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">WordPress translation plugin</a> that anyone can use.</p>\n<p>The interface allows you to easily translate the entire page at once, including output from shortcodes, forms and page builders. It also works out of the box with WooCommerce.</p>\n<p>Built the WordPress way, TranslatePress &#8211; Multilingual is a GPL and self hosted translation plugin, meaning you&#8217;ll own all your translations, forever. It&#8217;s the fastest way to create a bilingual or <a href=\"https://translatepress.com/how-to-create-a-multilingual-wordpress-site/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">multilingual site</a>.</p>\n<p><span class=\"embed-youtube\" style=\"text-align:center; display: block;\"><iframe class=\'youtube-player\' width=\'640\' height=\'360\' src=\'https://www.youtube.com/embed/pUlYisvBm8g?version=3&#038;rel=1&#038;showsearch=0&#038;showinfo=1&#038;iv_load_policy=1&#038;fs=1&#038;hl=en-US&#038;autohide=2&#038;wmode=transparent\' allowfullscreen=\'true\' style=\'border:0;\' sandbox=\'allow-scripts allow-same-origin allow-popups allow-presentation\'></iframe></span></p>\n<h3>Multilingual &amp; Translation Features</h3>\n<ul>\n<li>Translate all your website content directly from the front-end, in a friendly user interface (translations are displayed in real-time).</li>\n<li>Fully compatible with all themes and plugins</li>\n<li>Live preview of your translated pages, as you edit them.</li>\n<li><a href=\"https://translatepress.com/docs/image-translation/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">Image translation</a> support, for <a href=\"https://translatepress.com/translate-images-in-wordpress/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">translating images, sliders and other media</a>.</li>\n<li>Support for both manual and automatic translation</li>\n<li>Ability to <a href=\"https://translatepress.com/translate-dynamic-strings-wordpress/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">translate dynamic strings</a> (gettext) added by WordPress, plugins and themes.</li>\n<li>Integrates with Google Translate, allowing you to set up Automatic Translation using your own Google API key.</li>\n<li>Translate larger html blocks by merging strings into translation blocks.</li>\n<li>Place language switchers anywhere using shortcode <strong>[language-switcher]</strong>, WP menu item or as a floating dropdown.</li>\n<li>Editorial control allowing you to publish your language only when all your translations are done</li>\n<li>Conditional display content shortcode based on language [trp_language language=&#8221;en_US&#8221;] English content only [/trp_language]</li>\n<li>Possibility to <a href=\"https://translatepress.com/edit-plugin-strings/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">edit gettext strings</a> from themes and plugins from English to English, without adding another language. Basically a string-replace functionality.</li>\n<li>Translation Block feature in which you can translate multiple html elements together</li>\n<li>Native <strong>Gutenberg</strong> support, so you can easily <a href=\"https://translatepress.com/translate-gutenberg-blocks-in-wordpress/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">translate Gutenberg blocks</a></li>\n<li>Out of the box <a href=\"https://translatepress.com/translate-woocommerce-products-translatepress/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">WooCommerce</a> compatibility</li>\n</ul>\n<p>Note: this plugin uses the Google Translation API to translate the strings on your site. This feature can be enabled or disabled according to your preferences.</p>\n<p>Users with administrator rights have access to the following settings:</p>\n<ul>\n<li>select default language of the website and one translation language, for bilingual sites</li>\n<li>choose whether language switcher should display languages in their native names or English name</li>\n<li>force custom links to open in current language</li>\n<li>enable or disable url subdirectory for the default language</li>\n<li>enable automatic translation via Google Translate</li>\n</ul>\n<h3>Powerful Translation Add-ons</h3>\n<p>TranslatePress &#8211; Multilingual has a range of <a href=\"https://translatepress.com/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">premium Add-ons</a> that allow you to extend the power of this WordPress translation plugin:</p>\n<p><strong>Pro Add-ons</strong> (available in the <a href=\"https://translatepress.com/pricing/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">premium versions</a> only)</p>\n<ul>\n<li><a href=\"https://translatepress.com/docs/addons/multiple-languages/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">Extra Languages</a> &#8211; allows you to add an unlimited number of translation languages, with the possibility to publish languages later after you complete the translation</li>\n<li><a href=\"https://translatepress.com/docs/addons/seo-pack/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">SEO Pack</a> &#8211; lets you translate meta information (like page title, description, url slug, image alt tag, Twitter and Facebook Social Graph tags &amp; more) for boosting your multilingual SEO and increase traffic. Works with all popular SEO plugins.</li>\n<li><a href=\"https://translatepress.com/docs/addons/translator-accounts/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">Translator Accounts</a> &#8211; create or allow existing users to translate the site without admin rights</li>\n<li><a href=\"https://translatepress.com/docs/addons/browse-as-role/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">Browse as User Role</a> &#8211; view and translate content that is visible only to a particular user role</li>\n<li><a href=\"https://translatepress.com/docs/addons/navigate-based-language/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">Navigation Based on Language</a> &#8211; configure and display different menu items for different languages</li>\n<li><a href=\"https://translatepress.com/docs/addons/automatic-user-language-detection/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">Automatic User Language Detection</a> &#8211; redirect first time visitors to their preferred language based on their browser settings or IP address</li>\n<li><a href=\"https://translatepress.com/docs/addons/deepl-automatic-translation/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">DeepL Automatic Translation</a> &#8211; The DeepL Automatic Translation add-on lets you automatically translate your website through the DeepL API</li>\n</ul>\n<p><strong>Keyboard Shortcuts</strong></p>\n<ul>\n<li><strong>CTRL ( ⌘ ) + S</strong> – Save translation for the currently editing strings</li>\n<li><strong>CTRL ( ⌘ ) + ALT + Z</strong> – Discard all changes for the currently editing strings</li>\n<li><strong>CTRL ( ⌘ ) + ALT + →</strong> (Right Arrow) – Navigate to next string</li>\n<li><strong>CTRL ( ⌘ ) + ALT + ←</strong> (Left Arrow) – Navigate to previous string</li>\n</ul>\n<h4>Website</h4>\n<p><a href=\"https://translatepress.com/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">translatepress.com</a></p>\n<h4>Documentation</h4>\n<p><a href=\"https://translatepress.com/docs/translatepress/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">Visit TranslatePress WordPress Translation plugin documentation page</a></p>\n<h4>Add-ons</h4>\n<p><a href=\"https://translatepress.com/docs/translatepress/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">Add-ons</a></p>\n<h4>Demo Site</h4>\n<p>You can test out TranslatePress &#8211; Multilingual plugin by <a href=\"https://demo.translatepress.com/\" rel=\"nofollow ugc\">visiting our demo site</a></p>\n\";s:12:\"installation\";s:356:\"<ol>\n<li>Upload the translatepress folder to the &#8216;/wp-content/plugins/&#8217; directory</li>\n<li>Activate the plugin through the &#8216;Plugins&#8217; menu in WordPress</li>\n<li>Go to Settings -&gt; TranslatePress and choose a translation language.</li>\n<li>Open the front-end translation editor from the admin bar to translate your site.</li>\n</ol>\n\";s:3:\"faq\";s:2599:\"\n<dt id=\'where%20are%20my%20translations%20stored%3F\'>\nWhere are my translations stored?\n</h4>\n<p>\n<p>All the translation are stored locally in your server&#8217;s database.</p>\n</p>\n<dt id=\'what%20types%20of%20content%20can%20i%20translate%3F\'>\nWhat types of content can I translate?\n</h4>\n<p>\n<p>TranslatePress &#8211; Multilingual plugin works out of the box with WooCommerce, custom post types, complex themes and site builders, so you&#8217;ll be able to translate any type of content.</p>\n</p>\n<dt id=\'how%20is%20it%20different%20from%20other%20multilingual%20%26%20translation%20plugins%20like%20wpml%20or%20polylang%3F\'>\nHow is it different from other multilingual &amp; translation plugins like WPML or Polylang?\n</h4>\n<p>\n<p>TranslatePress is easier to use and more intuitive altogether. No more switching between the editor, string translation interfaces or badly translated plugins. You can now translate the full page content directly from the front-end. This makes TranslatePress a great alternative to plugins like Polylang and WPML.</p>\n</p>\n<dt id=\'how%20do%20i%20start%20to%20translate%20my%20wordpress%20site%3F\'>\nHow do I start to translate my WordPress site?\n</h4>\n<p>\n<p>After installing the plugin, select your secondary language and click &#8220;Translate Site&#8221; to start translating your entire site exactly as it looks in the front-end.</p>\n</p>\n<dt id=\'will%20it%20slow%20down%20my%20website%3F\'>\nWill it slow down my website?\n</h4>\n<p>\n<p>TranslatePress will have little impact on your site speed. For more details see <a href=\"https://translatepress.com/top-wordpress-translation-plugins-compared-based-on-page-load-time/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">Top WordPress Translation Plugins Compared Based on Page Load Time</a></p>\n</p>\n<dt id=\'can%20you%20translate%20your%20woocommerce%20store%3F\'>\nCan you translate your WooCommerce store?\n</h4>\n<p>\n<p>Yes, TranslatePress works out of the box with WooCommerce. You can use to build a <a href=\"https://translatepress.com/translate-woocommerce-products-translatepress/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">multilingual WooCommerce store</a>.</p>\n</p>\n<dt id=\'where%20can%20i%20find%20out%20more%20information%3F\'>\nWhere can I find out more information?\n</h4>\n<p>\n<p>For more information please check out our <a href=\"https://translatepress.com/docs/translatepress/?utm_source=wp.org&amp;utm_medium=tp-description-page&amp;utm_campaign=TPFree\" rel=\"nofollow ugc\">documentation</a>.</p>\n</p>\n\n\";s:9:\"changelog\";s:40456:\"<h4>1.9.5</h4>\n<ul>\n<li>Added setting that allows you to translate only certain paths or exclude certain paths from being translated.</li>\n<li>Fixed some cases where GET parameters were lost from links in translated languages</li>\n<li>WP Bakery integration that allow to include/exclude blocks for languages</li>\n<li>Added a compatibility for the MyListing theme.</li>\n<li>Load unsupported languages section only if an API key is saved.</li>\n<li>Add support for AIO SEO sitemap detection</li>\n</ul>\n<h4>1.9.4</h4>\n<ul>\n<li>Updated DOM parser to version 1.9.1, this should fix a bunch of broken HTML issues</li>\n<li>Fixed a case in Woocommerce where slugs weren&#8217;t translated from the seo-pack addon</li>\n<li>Fixed share link in Elementor blockquote Tweet button</li>\n<li>Fixed a possible issue with the search parameter being un-slashed</li>\n</ul>\n<h4>1.9.3</h4>\n<ul>\n<li>Compatibility with WooCommerce Min/Max Quantities plugin that added an attribute data-quantity two times on a link</li>\n<li>Added compatibility with Business Directory Plugin that was throwing 404</li>\n<li>Fixed some cases of unnecessary calls to automatic translation API-s</li>\n</ul>\n<h4>1.9.2</h4>\n<ul>\n<li>Fixed Norwegian language not working with Google Automatic Translation</li>\n<li>Fixed loading localization text-domain in different language than expected.</li>\n<li>Fixed gettext strings in incorrect language.</li>\n<li>Fixed WooCommerce country and states translations.</li>\n</ul>\n<h4>1.9.1</h4>\n<ul>\n<li>Fixing issue with WooCommerce slugs resulting in 404 if they were changed manually from the defaults in certain cases</li>\n<li>Added a new cleanup option in settings</li>\n<li>Fixed some issues with extra characters appearing on translated languages in certain conditions</li>\n<li>Always load Beaver Builder in default language.</li>\n</ul>\n<h4>1.9.0</h4>\n<ul>\n<li>Fixed some cases of multiple requests for finding out supported languages for automatic translation</li>\n</ul>\n<h4>1.8.9</h4>\n<ul>\n<li>Fixing search in xStore theme ajax search</li>\n<li>Fix edge cases of translation misalignment that caused some strings not to be translated and generated some notices</li>\n<li>Fix some cases of displaying uppercase trp-gettext wrappers</li>\n<li>Better handling of unsupported languages by automatic translation engine</li>\n</ul>\n<h4>1.8.8</h4>\n<ul>\n<li>Improved compatibility with Oxygen builder.</li>\n<li>Fixed a issue with pagination on blog page displaying page 0</li>\n</ul>\n<h4>1.8.7</h4>\n<ul>\n<li>Added region independent languages in hreflang meta tags</li>\n<li>Fixed JS error in Translation Editor for some type of anchor tags.</li>\n<li>Compatibility with WooCommerce myPOS gateway. Improved PayPal IPN handling in WooCommerce.</li>\n<li>Fixed issue with string in textarea being detected by our translation editor.</li>\n<li>We now keep pagination when changing languages on the blog page.</li>\n<li>Fixed a compatibility with xStore theme ajax search not appearing on other languages than english</li>\n<li>Fixed an issue with Woocommerce links in language switcher</li>\n<li>Compatibility with Woocommerce pdf Voucher plugin</li>\n<li>Fixed an issue with archive pages pagination for terms with translated slugs</li>\n<li>We now exclude some gettext strings from being translated that cause problems</li>\n</ul>\n<h4>1.8.6</h4>\n<ul>\n<li>Added optional &#8220;Powered by TranslatePress&#8221; in the floater language switcher. Would help us out a lot if you would enable this option in the settings. Thank You!</li>\n<li>Added a compatibility with Brizy Pro menu element</li>\n<li>Add current-menu-item CSS class to menus on translation languages</li>\n<li>Remove unused Delete command from String Translation</li>\n<li>Added compatibility with woocommerce-ultimate-pdf-invoices plugin</li>\n<li>Added compatibility with Uncode Theme menu on mobile</li>\n<li>Fix the infinite redirect when using subfolder for default language with the default WP 5.5 sitemap.</li>\n</ul>\n<h4>1.8.5</h4>\n<ul>\n<li>Improved SEO by making language switcher links crawlable</li>\n<li>Fixed Internal links issue with Use a Subdirectory for the default language enabled</li>\n<li>Added Elementor twitter button translation compatibility</li>\n<li>Added Elementor blockquote tweet button translation compatibility</li>\n<li>Added support for  PDF Embedder Premium Secure plugin</li>\n<li>Fixed a compatibility issue with Thrive theme</li>\n<li>Fixed a problem with Woocommerce order notes not being added on other languages</li>\n<li>Fixed a PHP warning</li>\n<li>Fixed issue where Oxigen repeater links were empty in the translated page</li>\n</ul>\n<h4>1.8.4</h4>\n<ul>\n<li>Fixed Slug Translation in SEO Addon not being able to translate anything due to js error related to flags</li>\n</ul>\n<h4>1.8.3</h4>\n<ul>\n<li>Added possibility to add custom languages</li>\n<li>Fixed Woocommerce issues with cart on certain settings</li>\n<li>Fixed some cases where translation blocks were not being created.</li>\n<li>Deactivated languages will only be accessible by admin or translator</li>\n<li>Compatibility with AddToAny Share buttons</li>\n<li>Compatibility with Classified listing search in translated languages</li>\n<li>Adjusted order of languages in floating language switcher according to position top or bottom</li>\n<li>Fixed some issues with automatic translation usage</li>\n</ul>\n<h4>1.8.2</h4>\n<ul>\n<li>Added compatibility with RECON gateway for WooCommerce</li>\n<li>Hooked changing locale to a higher priority. Fixes conflict with Divi theme</li>\n<li>Fix WooCommerce remove from cart action for certain languages</li>\n</ul>\n<h4>1.8.1</h4>\n<ul>\n<li>Fixed a problem with sitemap categories not being displayed correctly in Yoast SEO</li>\n<li>Changed a redirect status from 302 to 301 in certain cases</li>\n<li>Added support for Advanced WooCommerce Search</li>\n</ul>\n<h4>1.8.0</h4>\n<ul>\n<li>Fixed a compatibility issue with Thrive Architect plugin</li>\n<li>Fix compatibility with Woo Product Filter plugin</li>\n<li>Added compatibility with Oxygen Builder for search</li>\n<li>Fixed canonical links that were generated incorrectly in some cases</li>\n<li>Fix issue with hreflang incorrectly being detected due to clearing the ?trp parameter from the url when processing it</li>\n</ul>\n<h4>1.7.9</h4>\n<ul>\n<li>Fix for various 404 page results</li>\n<li>Fixed flag spacing in RTL languages</li>\n<li>Added &#8220;Full Language Names No HTML&#8221; option to language menu</li>\n<li>Added possibility to change date format by language</li>\n<li>Oxygen plugin stylesheet loading time improvement</li>\n<li>Compatibility with Brizy&#8217;s editor</li>\n<li>Added filter to allow https adjustments</li>\n<li>Improved searching on certain pages</li>\n</ul>\n<h4>1.7.8</h4>\n<ul>\n<li>Added possibility to exclude hreflang from an original URL</li>\n<li>Fixed translating json inside json. It ensures compatibility with Brizy</li>\n<li>Fixed http 500 response in trp-ajax calls</li>\n</ul>\n<h4>1.7.7</h4>\n<ul>\n<li>Added option to enable translation of numerals</li>\n<li>Added capability to translate special URLs like tel, callto or mailto</li>\n<li>Added support for translating oembed response data</li>\n<li>Fixed some CSS issues on Safari</li>\n</ul>\n<h4>1.7.6</h4>\n<ul>\n<li>Added a help wizard for the Visual Editor</li>\n<li>Added Puerto Rico flag</li>\n<li>Fixed issue with custom links on term archive pages where the term had a translated slug</li>\n<li>Added a recommended plugins section on the addons tab.</li>\n<li>Fixed a display issues in Strings interface for Safari</li>\n</ul>\n<h4>1.7.5</h4>\n<ul>\n<li>Dix incorrect opengraph url in Yoast SEO that caused fatal error with older versions</li>\n<li>Fix for blog page url slug translation</li>\n<li>Fix problem with updating Translator Accounts Add-on</li>\n<li>Fix incorrect number of parameters in wpseo_canonical filter</li>\n</ul>\n<h4>1.7.4</h4>\n<ul>\n<li>Fix fatal error with get_the_date filter due to other plugins calling incorrect params</li>\n<li>Fix issue with identical string in multiple places not showing all related strings</li>\n<li>Fixed Next and Previous buttons not working in the interface</li>\n<li>Fixed Yoast SEO canonical contain the correct language</li>\n<li>Fixed info icons too large before css loading</li>\n<li>Added compatibility with Superfly menu</li>\n<li>Add locale to user on user register and ignore a gettext string from WordPress and WooCommerce that added trpstr strings in the database</li>\n<li>Fix compatibility with Dflip plugin and Elementor Editor when a secondary language is the first one displayed</li>\n</ul>\n<h4>1.7.3</h4>\n<ul>\n<li>Fixed an error when WooCommerce is active and you access a term link that does not exist</li>\n<li>Added compatibility with SEO Pack version 1.2.4 or older</li>\n<li>Added compatibility with Affiliate Theme filters</li>\n</ul>\n<h4>1.7.2</h4>\n<ul>\n<li>Implemented mechanics to remove duplicate entries in the database</li>\n<li>Fixed issue with automatic translation time-outs on large pages</li>\n<li>Fixed an issue with escaped links in json responses from WooCommerce</li>\n<li>Fixed an issue with links inside gettext</li>\n</ul>\n<h4>1.7.1</h4>\n<ul>\n<li>Fixed issue with gettext when default language is different from WordPress default language</li>\n<li>Fixed Monthly/Yearly archives showing incorrect month/year</li>\n<li>Added message to activate Extra languages add-on when trying to add a new language.</li>\n</ul>\n<h4>1.7.0</h4>\n<ul>\n<li>Fixed compatibility issue with JetPack plugin in some cases</li>\n<li>Fixed JS undefined controls error for some setups</li>\n<li>Fixed translation memory not working properly for gettext in attributes</li>\n<li>Added support for Dublin Core meta-data</li>\n<li>Added support to ignore Elementor encoded url for popup action</li>\n<li>Improved queries for the database upgrade process</li>\n</ul>\n<h4>1.6.9</h4>\n<ul>\n<li>Fixed a conflict with Contact Form 7 plugin</li>\n<li>Fixed some broken html issues caused by a custom trp wrap. Some Revolution Slider plugin issues are fixed by this.</li>\n<li>Optimized some queries regarding search indexing to fix some performance problems. It should help with some Revolution Slider plugin performance issues.</li>\n<li>Fixed an issue with textarea in Advanced Options</li>\n<li>Exclude links from &#8220;Force language in custom links&#8221; by adding their selector in Settings -&gt; Advanced -&gt; Exclude selectors from translation</li>\n<li>Fixed force custom links in default language when add-subdirectory is set to yes and default language is not first language</li>\n<li>Better handling of href links with spaces</li>\n</ul>\n<h4>1.6.8</h4>\n<ul>\n<li>Added support for All In One SEO</li>\n<li>Added compatibility with Peepso double slashing translated links</li>\n<li>Add option in Advanced tab to disable translated links in sitemap</li>\n<li>Add options to disable post container tags in Advanced Tab.</li>\n<li>Organize Advanced Tabs by categories.</li>\n<li>Fix an error regarding the settings class in class-languages.php</li>\n<li>Fixed a filter in the trp_curpageurl() function</li>\n<li>Fix fatal error thrown when trying to access the global while it&#8217;s null in some cases</li>\n<li>Fixed x-default from advanced not working as expected</li>\n</ul>\n<h4>1.6.7</h4>\n<ul>\n<li>Improved the database update queries</li>\n<li>Fixed an error in mysql regarding post_parent_id meta insert if no id was found</li>\n<li>Added Rank Math support</li>\n<li>Added SEO Press support</li>\n<li>We now remove the trp-post-container before displaying the site</li>\n<li>Removed Get add-on from add-ons page</li>\n<li>Refactor support for SEO sitemaps so we don&#8217;t add the language to their URL&#8217;s</li>\n</ul>\n<h4>1.6.6</h4>\n<ul>\n<li>Implemented Search functionality in translated languages</li>\n<li>Added support for WooCommerce product search in translated languages</li>\n<li>Skip dynamic strings detection of ad links</li>\n<li>Added filters for adjusting gettext machine translation</li>\n</ul>\n<h4>1.6.5</h4>\n<ul>\n<li>Fixed Exclude words from automatic translation when many excluded words are added</li>\n<li>Compatibility with WooCommerce Fondy payment gateway</li>\n<li>Added filter to allow disabling of error manager</li>\n<li>Fixed a bug where you couldn&#8217;t deactivate a license to add a new one</li>\n</ul>\n<h4>1.6.4</h4>\n<ul>\n<li>Added a conditional Elementor block for language.</li>\n<li>Fixed Google Translate breaking some characters such as &amp; #8220;</li>\n<li>Improvements to error handling</li>\n<li>Fixed a js error</li>\n</ul>\n<h4>1.6.3</h4>\n<ul>\n<li>Fixed bug not being able to disable block crawlers option</li>\n<li>Improved UI on settings navigation tab</li>\n<li>Added extra information about how Deepl usage is paid</li>\n</ul>\n<h4>1.6.2</h4>\n<ul>\n<li>Fixed admin-bar appearing in translation editor</li>\n<li>Nextgen plugin compatibility fix</li>\n<li>Fixed a notice index translation-engine not set.</li>\n<li>Refactored upgrading settings.</li>\n<li>Solved issue with notifications shown in all backend that couldn&#8217;t be dismissed</li>\n<li>Added ability to detect if table is missing during requests. Show admin notice on all pages.</li>\n<li>Updated License message and Automatic Translation description</li>\n<li>Created notifications and error page. Disabled machine translations upon error.</li>\n</ul>\n<h4>1.6.1</h4>\n<ul>\n<li>Added support for hreflang x-default in Advanced Settings</li>\n<li>Improved checking if quota exceeded on google machine translation for each batch</li>\n<li>Fix a fatal error due to incorrect call to the_title filter made by some themes</li>\n<li>Fixed automatic translation character count not resetting daily</li>\n<li>Fixed possible incompatibility between trp_language shortcode and automatic translation</li>\n</ul>\n<h4>1.6.0</h4>\n<ul>\n<li>Gettext is no longer processed in REST API requests made from admin referer.</li>\n<li>Don&#8217;t update gettext machine translation with empty translation.</li>\n<li>Fixed machine translation codes not being set on first save settings</li>\n<li>Solved backwards compatibility with Deepl Add-on version 1.0.0</li>\n<li>We now don&#8217;t translate gettext into English.</li>\n<li>Fix: make sure we are not sending gettext non-unique array strings to Automatic Translation</li>\n<li>Fix: Add default translation engine if not available.</li>\n<li>Fix: hide Test API key button when machine translation is disabled or when there&#8217;s not API key saved.</li>\n</ul>\n<h4>1.5.9</h4>\n<ul>\n<li>Add an option to block crawlers from triggering automatic translations</li>\n<li>Added support for excluding words from automatic translation</li>\n<li>Compatibility with WooCommerce Checkout Add-Ons plugin</li>\n<li>Improved integration with WooCommerce PDF Invoices &amp; Packing Slips by translating product name and options in invoices</li>\n<li>Removed possible trp-wrap tags ending up in front-end content</li>\n<li>Improved display of license error messages</li>\n<li>Corrected and added missing flags for 7 languages</li>\n<li>Eliminate need for jQuery in language switcher shortcode</li>\n<li>Fixed issue with translating admin ajax JSON strings</li>\n<li>Fixed issue with  html tags breaking HTML</li>\n<li>Fixed warning in when saving advanced settings</li>\n<li>Fixed trpst characters in WooCommerce shipping calculator</li>\n<li>Fixed getting translations on fallback admin-ajax</li>\n<li>New integration with Deepl API Automatic translation in PRO versions</li>\n</ul>\n<h4>1.5.8</h4>\n<ul>\n<li>Added machine translation limit</li>\n<li>Added setting for translation memory limit</li>\n<li>Fixed encoding for the &#8220;Fix broken html feature&#8221;</li>\n<li>Fixed CSS for translation button in TP Editor</li>\n<li>Fixed disabling dynamic translation in translation editor when it is disabled from Advanced option</li>\n</ul>\n<h4>1.5.7</h4>\n<ul>\n<li>Modified an autoloader to prevent errors when manually updating the plugin</li>\n</ul>\n<h4>1.5.6</h4>\n<ul>\n<li>Improved speed on Gettext exclusion</li>\n<li>Make Gettext exclusion work without a domain in Advanced Settings</li>\n<li>Allow po/mo localization files to translate excluded Gettext strings</li>\n<li>Added Advanced setting to Exclude selectors from translation</li>\n<li>Added option to change floating language switcher position</li>\n<li>Added compatibility with CartFlows plugin</li>\n<li>Added compatibility with NextGen plugin</li>\n<li>Added compatibility with Ninja Popups plugin mails</li>\n<li>Added compatibility with Woo Tours plugin</li>\n<li>Fixed issue with trp-gettext wrappings in WooCommerce REST API</li>\n<li>Fixed issue with translating images run through JetPack CDN</li>\n<li>Fixed edge case where gettext inside script tag attributes was breaking html</li>\n<li>Translated title of product in WooCommerce &#8220;Product has been added to cart&#8221; message</li>\n<li>Better handling of string overdetection in dynamic string translation</li>\n<li>Better handling of sql errors and machine translation</li>\n<li>Fixed WooCommerce Product Translation on Cart Page for products with hyphen in their names</li>\n</ul>\n<h4>1.5.5</h4>\n<ul>\n<li>Fixed warnings regarding settings that appeared on fresh installs</li>\n</ul>\n<h4>1.5.4</h4>\n<ul>\n<li>Added Translation Memory feature</li>\n<li>Beaver Builder compatibility</li>\n<li>Fixed isseu with gettext special characters inside attributes breaking html sometimes</li>\n<li>Fixed an issue with urls slugs not being translated in site-map for secondary languages if &#8220;Use subdirectory on default language&#8221; was on</li>\n<li>Added support for display attribute for language-switcher shortcode</li>\n</ul>\n<h4>1.5.3</h4>\n<ul>\n<li>Fixed blank page when opening Translation Editor in some localized languages</li>\n</ul>\n<h4>1.5.2</h4>\n<ul>\n<li>Added -Advanced- tab with various custom settings</li>\n<li>Added compatibility to allow translating SeedProd plugin Coming Soon page</li>\n</ul>\n<h4>1.5.1</h4>\n<ul>\n<li>Added maximum possible size to srcset for translated images</li>\n<li>Added compatibility with Query Monitor plugin</li>\n<li>Better handling of licenses when pro addons are active</li>\n<li>Improved descriptions in settings page and other places</li>\n<li>Added filter to allow translation of href as an exception</li>\n<li>Fixed translation blocks not working on live in some edge cases</li>\n<li>Fixed translation block created in secondary language not working when strings were already translated</li>\n<li>Fixed JS error when dynamic translation is disabled by filter</li>\n<li>Fixed translating dynamic strings in Editor when viewing as Logged out</li>\n<li>Fixed page titles containing special characters not being translated</li>\n<li>Fixed title attribute not being translated</li>\n<li>Fixed certain custom WooCommerce permalinks not working on translated products</li>\n<li>Fixed pencil icon not showing for WooCommerce product images in Shop page on certain themes</li>\n<li>Fixed modifying wp_mail headers when we didn&#8217;t have to</li>\n</ul>\n<h4>1.5.0</h4>\n<ul>\n<li>Fixed some dynamic images not showing up in translated pages.</li>\n</ul>\n<h4>1.4.9</h4>\n<ul>\n<li>Fixed incompatibility with custom code for changing flags</li>\n<li>Fixed some pages not being translated due to incorrectly encoded character</li>\n<li>Fixed some images missing when automatic translation is on</li>\n</ul>\n<h4>1.4.8</h4>\n<ul>\n<li>Added support for translating images</li>\n<li>Added support for translating title attribute</li>\n<li>Added support for translating href pointing to internal files and href pointing to any external links</li>\n<li>Added support for translating attributes modified dynamically through JS</li>\n<li>Added support for translating multiple attributes on the same node</li>\n<li>Added support for translating nodes containing mixt of gettext and user-inputted strings</li>\n<li>Added notification and disabled TP for servers not running minimum PHP version 5.6.20</li>\n<li>Refactored and improved Translation Editor user interface</li>\n<li>Added Keyboard shortcuts: CTRL + S (save), CTRL + ALT + Z (discard all changes), CTRL + ALT + LEFT (previous string), CTRL + ALT + RIGHT (next string)</li>\n<li>Fixed issues with translation blocks not working on some instances</li>\n<li>Security improvements</li>\n</ul>\n<h4>1.4.7</h4>\n<ul>\n<li>Fixed a php error in previous commit</li>\n</ul>\n<h4>1.4.6</h4>\n<ul>\n<li>Fixed a js compatibility error with mootools.js</li>\n<li>Modified how the license page works and added plugin notifications</li>\n<li>Allow compatibility fix for Translation Editor on certain environments</li>\n<li>Fixed Safari bug with links when WooCommerce active</li>\n</ul>\n<h4>1.4.5</h4>\n<ul>\n<li>Performance improvements</li>\n<li>Fixed an issue that was causing empty strings to get inserted in the database</li>\n<li>Improvements to detecting dynamic js strings earlier</li>\n<li>Fixes some urls for sitemap</li>\n<li>We now check if str_get_html is successful to avoid fatal error</li>\n<li>Fixed regular string loaded by ajax not detected in translation editor</li>\n<li>We now allow translating WooCommerce product base name separately from selected variations</li>\n<li>Fixed WooCommerce cart details not being translated when changing language</li>\n<li>Fixed Automatic Google Translation on languages not published yet</li>\n<li>Fixed Translation Editor not working in default language when no translation language is published yet</li>\n<li>Fixed gettext wrapping characters showing up in WooCommerce Shipping taxes metabox on Order pages</li>\n</ul>\n<h4>1.4.4</h4>\n<ul>\n<li>Added more filters</li>\n<li>Make sure we do not insert empty strings in the gettext translation table</li>\n<li>Added support for Affiliate tracking</li>\n</ul>\n<h4>1.4.3</h4>\n<ul>\n<li>Fixed an issue with the Language by Get Parameter add-on</li>\n<li>Added compatibility with WooCommerce PDF invoice and WooCommerce&#8217;s order notes.</li>\n<li>Added stop_translating_page and before_running_hooks hooks.</li>\n<li>Refactored hooks-loader to easily remove hook</li>\n</ul>\n<h4>1.4.2</h4>\n<ul>\n<li>Fixes the issue with not being able to publish pages when Use subdirectory for default language is set to yes and Gutenberg is installed</li>\n<li>Fixed an issue with Elementor and Use subdirectory for default language set to yes</li>\n<li>Fixed an issue with Yoast Premium and Use subdirectory for default language set to yes</li>\n<li>Fixed missing spaces in translations for original gettext strings with untrimmed spaces</li>\n</ul>\n<h4>1.4.1</h4>\n<ul>\n<li>Added PHP 7.3 support</li>\n<li>Performance improvements</li>\n</ul>\n<h4>1.4.0</h4>\n<ul>\n<li>Added Enfold compatibility by increasing the template_include hook priority</li>\n<li>Add the costa rica flag</li>\n<li>Speed improvements by optimizing the full_trim function</li>\n<li>Added compatibility for WooCommerce Invoices plugins</li>\n<li>Fixed querying for dynamic strings in Translation Editor not bringing up translations for all languages</li>\n<li>Fixed notice when gettext table is empty</li>\n<li>Added function to display strings with bad encoding in Translation Editor</li>\n</ul>\n<h4>1.3.9</h4>\n<ul>\n<li>Fixed some issues with url translations</li>\n<li>Speed improvements</li>\n<li>Add Javanese flag</li>\n<li>Fixed issue with trimming dynamic strings in our own ajax calls</li>\n</ul>\n<h4>1.3.8</h4>\n<ul>\n<li>Speed improvements</li>\n<li>Remove notices from Editor when we don&#8217;t have translation languages</li>\n<li>Fixed notices with referrer in translator machine</li>\n<li>Fixed issues with urls in other languages</li>\n<li>Fix issue of nested gettext resulting in unwanted characters</li>\n<li>Strip gettext tags from urls run through sanitize_title and esc_url</li>\n<li>Set caching calls non-persistent. Doesn&#8217;t work with object caching otherwise</li>\n<li>Set lang attribute in html tag all the time including when on default language</li>\n<li>Refactored the way we are translating json</li>\n<li>Fixed issue with Woocommerce ajax calls</li>\n</ul>\n<h4>1.3.7</h4>\n<ul>\n<li>Fixed an issue with Woocommerce and redirects when the default language is not English</li>\n<li>Speed improvements</li>\n<li>Fix relative url without a trailingslash not getting a proper link back</li>\n<li>Add ?trp=edit-translation=preview to ajax loaded content. Also add it to all dynamic content.</li>\n<li>Added language code column in settings</li>\n<li>Removed async false from JS translate-dom-changes</li>\n</ul>\n<h4>1.3.6</h4>\n<ul>\n<li>Refactored the get_url_for_language() function which should fix a lot of problems with links</li>\n<li>Speed improvements</li>\n<li>Fixed translation block icon when creating a new block</li>\n<li>Fixed issues with trp tags leftovers in html</li>\n<li>Fixed issues with gettext strings that weren&#8217;t detected correctly</li>\n<li>Add support for relative url&#8217;s</li>\n<li>Added warning in settings about controlling costs of Google API</li>\n<li>Changed API key field description. Added feature to show/hide API key field based on Google Translate Active Yes/No</li>\n<li>Fixed Translated-dom-changes string not translated through trp-ajax.</li>\n<li>Fixed 400 errors in GT API</li>\n</ul>\n<h4>1.3.5</h4>\n<ul>\n<li>Fixed translation problems introduced in the last two versions</li>\n<li>Added a console message when trp-ajax request uses fall back to admin ajax for debugging purposes.</li>\n</ul>\n<h4>1.3.4</h4>\n<ul>\n<li>Fixed issue with options in select tag that were not translatable</li>\n<li>Fixed force language in custom links</li>\n<li>Fixed Woocommerce links fpr products or categories that were added by the user manually in a page</li>\n<li>Added Settings link to the list of links displayed on Plugins page</li>\n<li>Fixed issue with Kazakhstan flag</li>\n</ul>\n<h4>1.3.3</h4>\n<ul>\n<li>Fixed issue with Woocommerce ajax strings that were broken in editor on default language in some cases</li>\n<li>Speed improvements</li>\n</ul>\n<h4>1.3.2</h4>\n<ul>\n<li>Speed improvements</li>\n<li>Add support for the Ginger – EU Cookie Law plugin</li>\n<li>Add support for data-no-dynamic-translation attribute that skips dynamic strings from being translated by dom changes detector</li>\n<li>Fixed Edit Pencil icon css in Translation Editor for some sites</li>\n<li>Refactored the way we add trp-gettext tag. This should have a lot of benefits in compatibility with other plugins</li>\n<li>Optimized block translation detection</li>\n<li>Added caching to trp_x function when reading external .mo files</li>\n<li>Fixed issue with TP icon css that was broken on wpforms forms</li>\n<li>Added secret page for removing duplicate rows from database: wp-admin/admin.php?page=trp_remove_duplicate_rows</li>\n</ul>\n<h4>1.3.1</h4>\n<ul>\n<li>Fixed Woocommerce translation of permalinks</li>\n<li>Added support for remove_accents to be based on default language when called from the sanitize_title function</li>\n<li>Added support for translating JSON found in custom ajax request</li>\n<li>Added better REST compatibility</li>\n<li>Added compatibility for Peepso plugin</li>\n<li>Fixed broken link to google translate set up api key on settings page</li>\n<li>Corrected flags for Arabic and Bengali languages</li>\n<li>Fixed issue with multiple slashes being added when the URL had extra get parameters</li>\n</ul>\n<h4>1.3.0</h4>\n<ul>\n<li>Added support for word trim when the default language is japanese, chinese or thai.</li>\n<li>Exluded wp_trim_words funtion from our gettext filter to prevent som issues</li>\n<li>Fixed an issue with gettext inside attributes that passed through the wp_kses function.</li>\n<li>Fixed issues with the Customizer</li>\n<li>Added padding to the language switcher image so we don&#8217;t conflict with themes that add extra padding to images inside links</li>\n<li>We no longer remove \\r \\n \\t from the translation</li>\n<li>Fixed issue with title attribute that contained html</li>\n<li>Added a filter to all href attributes detected on our translation page</li>\n<li>Added a notice to inform admins of the missing mbstring php library</li>\n<li>We now send all error logs to debug.log</li>\n<li>Added cite and blockquote as top_parents for merge rule on Translation blocks</li>\n<li>Fixed nonce accidentally being passed through internationalized function</li>\n</ul>\n<h4>1.2.9</h4>\n<ul>\n<li>Rearranged and renamed some languages in the options dropdown</li>\n<li>Fixed flag of Khmer language</li>\n<li>Added Automatic Language Detection notice and included it on add-ons page</li>\n<li>Fixed an issue with WooCommerce checkout and Stripe Gateway</li>\n<li>Fixed issues with some improper responses from the WP Remote API functions</li>\n<li>Fixed minor issues with ajax</li>\n</ul>\n<h4>1.2.8</h4>\n<ul>\n<li>Added a lot of hooks in the translation manager interface so other people can insert new content there.</li>\n<li>We now take into account the presence of www or lack of it in custom links that might be local</li>\n<li>We now make sure we&#8217;re not changing the locale in the backend if the language order is different.</li>\n<li>Fixed issue with incorrect language adding in the backend that caused notices in the front-end</li>\n<li>Removed obsolete function add_cookie</li>\n<li>Fixed trailingslashit over get_permalink in url-converter</li>\n<li>Removed adding cookie from php. Fixed enqueue_styles on license and add-ons tabs. Removed deprecated function.</li>\n</ul>\n<h4>1.2.7</h4>\n<ul>\n<li>Added a warning when changing the default language that it will invalidate their existing translations</li>\n<li>Fixed incorrect detection of the form action language parameter</li>\n<li>Improved compatibility with themes and plugins that use object buffering</li>\n<li>Fixed some issues with image urls</li>\n</ul>\n<h4>1.2.6</h4>\n<ul>\n<li>Refactored determining language, redirecting and cookie adding</li>\n<li>Removed leftover trp-gettext tags when WooCommerce is active on some pages</li>\n<li>Fixed get_url_for_language function that was having problems in some cases.</li>\n</ul>\n<h4>1.2.5</h4>\n<ul>\n<li>Fixed DOM changes script not being enqueued anymore</li>\n</ul>\n<h4>1.2.4</h4>\n<ul>\n<li>Refactor the shortcode language switcher so it&#8217;s now HTML similar to the floater</li>\n<li>Added link to Appearance -&gt; menus in Settings page</li>\n<li>Fixed language redirect with permalinks so custom parameters are passed correctly back to the url</li>\n<li>Do not load dynamic string translation for IE11 and older</li>\n</ul>\n<h4>1.2.3</h4>\n<ul>\n<li>Fixed back-end css style not being targeted only for TP Settings page</li>\n<li>Add filter to not remove detected dynamic strings until the ajax is finished</li>\n<li>Fixed data-no-translation not taken into account in some cases of Dynamic strings</li>\n<li>Fixed translated slug not being included in url sometimes</li>\n<li>Fixed issue with gettext string on non visible html attr that prevented other attr from being translated</li>\n<li>Fixed bug with translating dom changes not working for complex HTML hierarchy</li>\n<li>Corrected flag for Afrikaans.</li>\n<li>Fixed compatibility issues with older jQuery versions</li>\n</ul>\n<h4>1.2.2</h4>\n<ul>\n<li>Added Translation Block feature for translating multiple html elements together</li>\n<li>Improvement: make it possible for the SEO Addon to automatically translate page slugs using Google Translate</li>\n<li>Fix: using the shortcode language switcher added #trpprocessurl to the end of the url</li>\n<li>Fix: changing languages from a secondary language gave 404 page when the page slug was translated</li>\n<li>Fix: submitting a form from one page to another directed the user to the default language. Now if Force Custom Language Links is enabled the user gets directed to the correct url</li>\n</ul>\n<h4>1.2.1</h4>\n<ul>\n<li>Extra css for the floater images so they don&#8217;t brake the line in certain themes</li>\n<li>Fixed compatibility issue with Woocommerce cart widget</li>\n<li>Fix: use the siteurl when the homeurl is empty to detect the language</li>\n</ul>\n<h4>1.2.0</h4>\n<ul>\n<li>Fix wptexturize changing characters in secondary languages</li>\n<li>Mini refactoring of the url_is_file() function</li>\n<li>Refactor get_url_for_language() to not use the global  var</li>\n<li>We no longer add the language path to links to actual files on the server</li>\n</ul>\n<h4>1.1.9</h4>\n<ul>\n<li>Fix widget language switcher to take into account the new hreflang</li>\n</ul>\n<h4>1.1.8</h4>\n<ul>\n<li>Fixed bug with Strings List appearing also in Dynamic Strings List. Also fixed bug with duplicate dynamic strings not having a pencil icon because of missing jquery_object.</li>\n<li>Fixed Woocommerce session storage compatibility</li>\n<li>Fixed language floater not opening on iPhone.</li>\n<li>Fixed issue with normal text nodes that were inside an element that had an atribute with gettext and did not get translated</li>\n<li>Replaced _ with &#8211; in hreflang and filter it</li>\n<li>Make force language to custom links set to yes as a default</li>\n<li>Remove intensive functions from inside two loops so they only happen once we detect something in js</li>\n<li>Decode html characters in mutation observed strings and removed stripslashes from trp-ajax.php to fix some strings added with js not being translatable</li>\n</ul>\n<h4>1.1.7</h4>\n<ul>\n<li>Compatibility fix with Elementor Page Builder</li>\n<li>Added translation .pot file</li>\n<li>Add proper encoding for mysqli connection in our trp-ajax.php file so we fixed some translation</li>\n<li>Fixed infinite loop related to mutation observer on javascript strings by not re-adding detected strings again if they are similar to existing ones</li>\n<li>Aligned text from add-ons tab.</li>\n</ul>\n<h4>1.1.6</h4>\n<ul>\n<li>Added support for translating Contact Form 7 alert messages</li>\n<li>Fixed issue with some strings containing special characters not being translatable (i.e. &#8220;¿¡&#8221;)</li>\n<li>Fixed bug with switching languages in Editor when viewing as Logged out</li>\n<li>Fixed issue with broken homepage links in some themes</li>\n<li>Added support for RTL languages in translation editor</li>\n</ul>\n<h4>1.1.5</h4>\n<ul>\n<li>Added support for translation blocks using the css class .translation-block.</li>\n<li>Added possibility to choose a different language as default language seen by website visitors.</li>\n<li>Updated add-ons settings page with the missing add-ons, added Language by GET parameter addon</li>\n<li>Fixed issue with the [language-switcher] in a post or page that broke saving the page when Yoast SEO plugin is active</li>\n<li>Added a plugin notification class and a notification for pretty permalinks</li>\n<li>Added WooCommerce compatibility tag</li>\n<li>Small css improvements</li>\n</ul>\n<h4>1.1.4</h4>\n<ul>\n<li>Filter to allow adding new language: &#8216;trp_wp_languages&#8217;</li>\n<li>Fixed issue with get_permalink() in ajax calls that was not returning the propper language</li>\n<li>Adapted code to allow language based on a GET parameter</li>\n<li>Fix some issues with language switcher and custom queries as well as take into account if subdirectory for default language is on</li>\n<li>Fixed issue with js translation that was trimming numbers and other characters from strings when it shouldn&#8217;t</li>\n</ul>\n<h4>1.1.3</h4>\n<ul>\n<li>Fix issue where the language switcher didn&#8217;t work for BuddyPress pages</li>\n<li>Fixed issue with CDATA in post content that was breaking the translation</li>\n<li>Added a filter that can be activated and that tries to fix invalid html: trp_try_fixing_invalid_html</li>\n</ul>\n<h4>1.1.2</h4>\n<ul>\n<li>We now make sure that all forms when submitted redirect to the correct language</li>\n<li>Fixed an issue with missing slash from language switcher</li>\n<li>Fixed an issue where we were not redirecting to the correct url slug when switching languages</li>\n<li>Fixed a possible notice inside the get_language_names function</li>\n<li>Fixed html breaking because of unescaped quotes in translated meta content</li>\n<li>Removed a special character from the full_trim function that was causing some strings to not be selectable for translation</li>\n</ul>\n<h4>1.1.1</h4>\n<ul>\n<li>Fixed js error with startsWith method not being supported in IE</li>\n<li>Removed unnecessary files from select2 lib</li>\n<li>Improved the way we rewrite urls to remove certain bugs</li>\n</ul>\n<h4>1.1.0</h4>\n<ul>\n<li>Implemented View As &#8220;Logged out user&#8221; functionality for translating strings that show only for logged out users</li>\n<li>Allow slug edit for default language</li>\n<li>Fixed an issue with the dropdown of translation strings when there were unsaved changes and the dropdown disconected from the textarea</li>\n<li>Prevent translation editor icon pencil to exit the translation iframe</li>\n<li>Fixed translating via the next/prev buttons that reset the position in the translation string list</li>\n<li>Refactor the way we are generating the language url for the language switcher when we don&#8217;t have a variable available</li>\n</ul>\n<h4>1.0.9</h4>\n<ul>\n<li>We now flush permalinks when saving the settings page to avoid getting 404 and 500 errors</li>\n<li>Added the current language as a class on the body tag. Ex: translatepress-en_US</li>\n<li>Small readme changes</li>\n</ul>\n<h4>1.0.8</h4>\n<ul>\n<li>We now allow HTML in normal strings translations.</li>\n<li>Changed the way we get the default language permalinks in Woocommerce rewrites</li>\n<li>Fixed issues with date_i18n function</li>\n<li>Fixed a warning generated when there are no rewrite rules</li>\n<li>Fixed dynamic strings not updating the translation dropdown list.</li>\n<li>Fixed issues with hidden space characters that were breaking some translations</li>\n<li>Make sure we don&#8217;t loose the trp-edit-translation=preview from url after a WordPress redirect</li>\n</ul>\n<h4>1.0.7</h4>\n<ul>\n<li>Fixed a small bug in js regarding the translation editor sidebar with</li>\n<li>Fixed Language Switcher for Woocommerce product categories and product tags going to 404 pages</li>\n<li>Fixed issues with Woocommerce and permalinks when the default language was not english</li>\n<li>Excluded more functions from getting gettext wraps</li>\n</ul>\n<h4>1.0.6</h4>\n<ul>\n<li>Added filter on capabilities to allow other roles to edit translations:&#8217;trp_translating_capability&#8217;</li>\n<li>Don&#8217;t show php errors and notices when we are storing strings in the database</li>\n<li>Fixed issues with attributes that contain json content, for instance in woocommerce variations</li>\n<li>We no longer wrap gettext inside the wptexturize function</li>\n<li>We no longer wrap gettexts that appear in the bloginfo function</li>\n</ul>\n<h4>1.0.5</h4>\n<ul>\n<li>Added possibility to edit gettext strings from themes and plugins from english to english, without adding another language. Basically, string-replace functionality.</li>\n<li>Support for translating text input placeholders</li>\n<li>We have a way of translating emails using the conditional language shortcode [trp_language language=&#8221;en_US&#8221;] English only content [/trp_language]</li>\n<li>Fixed issues with some elements that contained new lines and \\u00a0 at the start of the strings</li>\n</ul>\n<h4>1.0.4</h4>\n<ul>\n<li>Fixed issues with the pencil select icon in the translation editor not showing up in certain cases on the button element</li>\n<li>Fixed issues with the pencil select icon in the translation editor not showing up in certain cases because of overflow hidden</li>\n<li>Fixed a issue that was sometimes causing javascript errors with certain plugins</li>\n</ul>\n<h4>1.0.3</h4>\n<ul>\n<li>Added a conditional language shortcode: [trp_language language=&#8221;en_US&#8221;] English only content [/trp_language]</li>\n<li>Create link to test out Google API key.</li>\n<li>Improvements to Woocommerce compatibility</li>\n<li>Fixed json_encode error that was causing js errors</li>\n<li>Changed &#8216;template_include&#8217; hook priority to improve compatibility with some themes</li>\n</ul>\n<h4>1.0.2</h4>\n<ul>\n<li>Translation interface improvements</li>\n<li>Fixed issues with strings loaded with ajax</li>\n<li>Added an addon page</li>\n<li>Fixed bug with gettext node accidentally wrapping another string too.</li>\n</ul>\n<h4>1.0.1</h4>\n<ul>\n<li>Fixed incorrect blog prefix name for Multisite subsites on admin_bar gettext hook.</li>\n<li>Fixed Translate Page admin bar button sometimes not having the correct url for entering TP Editor Mode</li>\n<li>Skipped dynamic strings that have only numbers and special characters.</li>\n<li>Fixed order of categories in Editor dropdown. (Meta Information, String List..)</li>\n<li>Fixed JS error Uncaught Error: Syntax error, unrecognized expression</li>\n</ul>\n<h4>1.0.0</h4>\n<ul>\n<li>Initial release.</li>\n</ul>\n\";s:11:\"screenshots\";s:2345:\"<ol><li><a href=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-1.gif?rev=1818213\"><img src=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-1.gif?rev=1818213\" alt=\"TranslatePress front-end visual translation editor in action\"></a><p>TranslatePress front-end visual translation editor in action</p></li><li><a href=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-2.png?rev=1818213\"><img src=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-2.png?rev=1818213\" alt=\"Front-end translation editor used to translate the entire page content\"></a><p>Front-end translation editor used to translate the entire page content</p></li><li><a href=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-3.png?rev=1818213\"><img src=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-3.png?rev=1818213\" alt=\"How to translate a dynamic string (gettext)\"></a><p>How to translate a dynamic string (gettext)</p></li><li><a href=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-4.png?rev=2113591\"><img src=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-4.png?rev=2113591\" alt=\"Translating WooCommerce Products for your multilingual store\"></a><p>Translating WooCommerce Products for your multilingual store</p></li><li><a href=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-5.png?rev=2113591\"><img src=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-5.png?rev=2113591\" alt=\"Translate Images and Image Sliders\"></a><p>Translate Images and Image Sliders</p></li><li><a href=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-6.png?rev=2113591\"><img src=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-6.png?rev=2113591\" alt=\"Settings Page\"></a><p>Settings Page</p></li><li><a href=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-7.png?rev=2113591\"><img src=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-7.png?rev=2113591\" alt=\"Floating Language Switcher\"></a><p>Floating Language Switcher</p></li><li><a href=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-8.png?rev=2113591\"><img src=\"https://ps.w.org/translatepress-multilingual/assets/screenshot-8.png?rev=2113591\" alt=\"Menu Language Switcher\"></a><p>Menu Language Switcher</p></li></ol>\";s:7:\"reviews\";s:15559:\"<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Great plugin!</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/lygrenhelene\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/340890df5d5b877381eae112cb957a67?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/340890df5d5b877381eae112cb957a67?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/lygrenhelene\" class=\"reviewer-name\">lygrenhelene</a> on <span class=\"review-date\">February 25, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">I recommend this plugin as it is very easy to use, no big hassle or difficult translation, and support is great at helping me with issues. It was a bit tricky with the translation of videos but it was due to my DIVI theme which I resolved with them. Both helped me succeed with translating videos (changing video depending on the language). So I feel theres nothing you cant do with this translation tool although my site was small enough to Translate manually. Its still possible to Translate automatically with good precision but I didnt really need it. All in all I recommend this for its simplicity and zero headache!!! Thanks Translatepress for a good job done </div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Magic and Speed Brake at the same time</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"3 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"3\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/nicmare\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/296b422da40e9170cef95651a19f559b?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/296b422da40e9170cef95651a19f559b?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/nicmare\" class=\"reviewer-name\">nicmare</a> on <span class=\"review-date\">February 18, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">This plugin is pure magic. On simple pages, it works great. Translation is very intuitive and comfortable. Really a banger! Never seen this before. All in all works good right out of the box.\n\nBut on the other hand with the POV of a developer you are not really aware what is going on here. Documentation is very simple. Support is lazy, there are a lot of new database tables and entries. Seems the serverside .po and .mo files get only read once and then ignored. Page speed decreases dramatically if you have complex pages!!! This is a no go ! they seem to use javascript ajax calls for translation in the frontend - weird! so if javascript will is not loaded, translation might missing. \n\nat the moment it\'s no option to pay for the premium version. and it seems support is not getting better if you read the 1* reviews. thats really a pity. \n\nbut i am sure it will get better in each issue! at the moment i better stick to polylang.</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Best translation plugin I&#039;ve ever used!</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/frobertgraphiste\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/404dff6cb9a78e93541343def2fa2686?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/404dff6cb9a78e93541343def2fa2686?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/frobertgraphiste\" class=\"reviewer-name\">frobertgraphiste</a> on <span class=\"review-date\">February 18, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">I\'ve tried all of the most popular translation options available for Wordpress and this is by far the best one. The way it translates the content is clearly the best way to translate a website.\n\nThe only thing I would like is to reduce the extra plugins we need for the add-ons. </div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Good plugin by the simple way</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/ktownindex\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/d70bf588aef0fc11a21bcc5e24299d41?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/d70bf588aef0fc11a21bcc5e24299d41?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/ktownindex\" class=\"reviewer-name\">ktownindex</a> on <span class=\"review-date\">February 10, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">I\'ve tried this and that translation plugin to add one language, but it seems to give the most satisfying results. It is easy to understand and translation is also supported with various options, so I am very satisfied. Only thanks to the production team.</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">A Plugin which does what it claims.</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/randon\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/9818c255c4eab1e2a915461999277b52?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/9818c255c4eab1e2a915461999277b52?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/randon\" class=\"reviewer-name\">randon</a> on <span class=\"review-date\">February 10, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">A Plugin which does what it claims. \nGreat Support</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Satisfied with Pro version</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/olganey\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/cb3c775ebedb1fab6e62b431e91f3f53?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/cb3c775ebedb1fab6e62b431e91f3f53?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/olganey\" class=\"reviewer-name\">olganey</a> on <span class=\"review-date\">February 10, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">I used the free version first but then switched to the Pro version to be able to translate SEO.\n- Needed some help in inserting different videos for each language and fixing some issues specific to the Russian language.\n- Was not able to find the right tutorials but got help from support. They are not replying fast but know how to fix any issues.\n\nI can recommend using TranslatePress!\n</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">very good</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/natkab\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/917630e53fd01e81188e44a0714aa88d?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/917630e53fd01e81188e44a0714aa88d?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/natkab\" class=\"reviewer-name\">natkab</a> on <span class=\"review-date\">February 10, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Super. Easy to work. Perfect support</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Great and easy!</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/artprofile\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/ed75865ddd30603643f792f92d13311b?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/ed75865ddd30603643f792f92d13311b?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/artprofile\" class=\"reviewer-name\">Artprofile</a> on <span class=\"review-date\">February 8, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Recommended for small site!  Very easy to use! Thanks!</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Very good customer support</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/fvoelkel\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/3ea021314ea7442b05a343fe15d12c60?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/3ea021314ea7442b05a343fe15d12c60?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/fvoelkel\" class=\"reviewer-name\">fvoelkel</a> on <span class=\"review-date\">February 2, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Tool does what it is supposed to do! Very easy to handle. Customer support is extremely quick and helpful.</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Very Easy to translate</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/atiq97\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/87ad5e9df70198ccd2fb117a869c5413?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/87ad5e9df70198ccd2fb117a869c5413?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/atiq97\" class=\"reviewer-name\">atiq97</a> on <span class=\"review-date\">February 2, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">This plugin is awesome. With the free version, you can literally do everything, which other plugins offer in huge pricing. \n\nI had a small issue and I wanted to solve it with plugin options ( instead of coding ) and the team helped me quickly on the wp forum. Thanks again</div>\n</div>\n\";}s:17:\"short_description\";s:105:\"Translate your entire site directly from the front-end and go multilingual, with full support for&hellip;\";s:13:\"download_link\";s:76:\"https://downloads.wordpress.org/plugin/translatepress-multilingual.1.9.5.zip\";s:11:\"screenshots\";a:8:{i:1;a:2:{s:3:\"src\";s:80:\"https://ps.w.org/translatepress-multilingual/assets/screenshot-1.gif?rev=1818213\";s:7:\"caption\";s:60:\"TranslatePress front-end visual translation editor in action\";}i:2;a:2:{s:3:\"src\";s:80:\"https://ps.w.org/translatepress-multilingual/assets/screenshot-2.png?rev=1818213\";s:7:\"caption\";s:70:\"Front-end translation editor used to translate the entire page content\";}i:3;a:2:{s:3:\"src\";s:80:\"https://ps.w.org/translatepress-multilingual/assets/screenshot-3.png?rev=1818213\";s:7:\"caption\";s:43:\"How to translate a dynamic string (gettext)\";}i:4;a:2:{s:3:\"src\";s:80:\"https://ps.w.org/translatepress-multilingual/assets/screenshot-4.png?rev=2113591\";s:7:\"caption\";s:60:\"Translating WooCommerce Products for your multilingual store\";}i:5;a:2:{s:3:\"src\";s:80:\"https://ps.w.org/translatepress-multilingual/assets/screenshot-5.png?rev=2113591\";s:7:\"caption\";s:34:\"Translate Images and Image Sliders\";}i:6;a:2:{s:3:\"src\";s:80:\"https://ps.w.org/translatepress-multilingual/assets/screenshot-6.png?rev=2113591\";s:7:\"caption\";s:13:\"Settings Page\";}i:7;a:2:{s:3:\"src\";s:80:\"https://ps.w.org/translatepress-multilingual/assets/screenshot-7.png?rev=2113591\";s:7:\"caption\";s:26:\"Floating Language Switcher\";}i:8;a:2:{s:3:\"src\";s:80:\"https://ps.w.org/translatepress-multilingual/assets/screenshot-8.png?rev=2113591\";s:7:\"caption\";s:22:\"Menu Language Switcher\";}}s:8:\"versions\";a:97:{s:5:\"1.0.0\";s:76:\"https://downloads.wordpress.org/plugin/translatepress-multilingual.1.0.0.zip\";s:5:\"1.0.1\";s:7